import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getCourses, deleteCourse } from '../services/courseService';

const ManageCourses = () => {
  const [courses, setCourses] = useState([]);
  const [action, setAction] = useState(''); // Estado para manejar la acción seleccionada
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCourses = async () => {
      const data = await getCourses();
      setCourses(data);
    };

    fetchCourses();
  }, []);

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      await deleteCourse(courseId, localStorage.getItem('token'));
      setCourses(courses.filter(course => course.ID !== courseId));
    }
  };

  const handleAction = (action, courseId) => {
    setAction(action);
    if (action === 'create') {
      navigate('/create-course');
    } else if (action === 'edit') {
      navigate(`/edit-course/${courseId}`);
    }
  };

  if (action !== '') {
    return null;
  }

  return (
    <div>
      <h1>Manage Courses</h1>
      <button onClick={() => handleAction('create')}>Create Course</button>
      <ul>
        {courses.map(course => (
          <li key={course.ID}>
            {course.title}
            <button onClick={() => handleAction('edit', course.ID)}>Edit</button>
            <button onClick={() => handleDelete(course.ID)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageCourses;
