import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getCourseById, enrollInCourse, addComment, uploadFile, getComments, getFiles } from '../services/courseService';
import './CourseDetail.css';

const CourseDetail = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [enrolled, setEnrolled] = useState(false);
  const [showCongrats, setShowCongrats] = useState(false); // Estado para el mensaje de confirmación
  const [comment, setComment] = useState('');
  const [file, setFile] = useState(null);
  const [comments, setComments] = useState([]);
  const [files, setFiles] = useState([]);
  const token = localStorage.getItem('token'); // Obtener el token del almacenamiento local

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const data = await getCourseById(id);
        setCourse(data);
        const commentsData = await getComments(id, token);
        setComments(commentsData);
        const filesData = await getFiles(id, token);
        setFiles(filesData);
      } catch (error) {
        console.error('Error fetching course:', error);
      }
    };

    fetchCourse();
  }, [id, token]);

  const handleEnroll = async () => {
    try {
      await enrollInCourse(parseInt(id), token);
      setEnrolled(true); // Actualizar el estado para indicar que el usuario se ha inscrito
      setShowCongrats(true); // Mostrar el mensaje de confirmación
    } catch (error) {
      console.error('Error enrolling in course:', error);
      alert('You are already enrolled in this course.');
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      const newComment = await addComment(id, comment, token);
      setComments([...comments, newComment]);
      setComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    try {
      const uploadedFile = await uploadFile(id, formData, token);
      setFiles([...files, uploadedFile]);
      setFile(null);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div className="course-detail-container">
      <div className="course-detail-card">
        <h1 className="course-title">{course.title}</h1>
        <p className="course-description">{course.description}</p>
        <p className="course-instructor">Instructor: {course.instructor}</p>
        <p className="course-duration">Duration: {course.duration} hours</p>
        <p className="course-requirements">Requirements: {course.requirements}</p>
        {!enrolled && (
          <button className="enroll-button" onClick={handleEnroll}>
            Enroll in this course
          </button>
        )}
        {showCongrats && ( // Mostrar el mensaje de confirmación si showCongrats es true
          <div className="congrats-message">
            Congrats, you have been enrolled!
          </div>
        )}
        <div className="comments-section">
          <h2>Comments</h2>
          <form onSubmit={handleCommentSubmit}>
            <div>
              <label>Comment:</label>
              <textarea value={comment} onChange={(e) => setComment(e.target.value)} required />
            </div>
            <button type="submit">Add Comment</button>
          </form>
          <ul>
            {comments.map((c) => (
              <li key={c.ID}>{c.comment}</li>
            ))}
          </ul>
        </div>
        <div className="files-section">
          <h2>Files</h2>
          <form onSubmit={handleFileSubmit}>
            <div>
              <label>Upload File:</label>
              <input type="file" onChange={handleFileChange} required />
            </div>
            <button type="submit">Upload</button>
          </form>
          <ul>
            {files.map((f) => (
              <li key={f.ID}><a href={`/${f.file_path}`} target="_blank" rel="noopener noreferrer">View File</a></li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
