import React, { useState, useEffect } from 'react';
import { getCourses, createCourse, updateCourse, deleteCourse } from '../services/courseService';

const ManageCourses = () => {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({
    title: '',
    description: '',
    instructor: '',
    duration: 0,
    requirements: '',
  });
  const token = localStorage.getItem('token'); // Obtener el token del almacenamiento local

  useEffect(() => {
    const fetchCourses = async () => {
      const data = await getCourses();
      setCourses(data);
    };

    fetchCourses();
  }, []);

  const handleCreateCourse = async () => {
    const createdCourse = await createCourse(newCourse, token);
    setCourses([...courses, createdCourse]);
    setNewCourse({
      title: '',
      description: '',
      instructor: '',
      duration: 0,
      requirements: '',
    });
  };

  const handleUpdateCourse = async (id) => {
    const updatedCourse = await updateCourse(id, newCourse, token);
    setCourses(courses.map(course => (course.ID === id ? updatedCourse : course)));
  };

  const handleDeleteCourse = async (id) => {
    await deleteCourse(id, token);
    setCourses(courses.filter(course => course.ID !== id));
  };

  return (
    <div>
      <h1>Manage Courses</h1>
      <div>
        <h2>Create New Course</h2>
        <input
          type="text"
          placeholder="Title"
          value={newCourse.title}
          onChange={(e) => setNewCourse({ ...newCourse, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newCourse.description}
          onChange={(e) => setNewCourse({ ...newCourse, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Instructor"
          value={newCourse.instructor}
          onChange={(e) => setNewCourse({ ...newCourse, instructor: e.target.value })}
        />
        <input
          type="number"
          placeholder="Duration"
          value={newCourse.duration}
          onChange={(e) => setNewCourse({ ...newCourse, duration: parseInt(e.target.value, 10) })}
        />
        <input
          type="text"
          placeholder="Requirements"
          value={newCourse.requirements}
          onChange={(e) => setNewCourse({ ...newCourse, requirements: e.target.value })}
        />
        <button onClick={handleCreateCourse}>Create Course</button>
      </div>
      <div>
        <h2>Existing Courses</h2>
        <ul>
          {courses.map(course => (
            <li key={course.ID}>
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <p>{course.instructor}</p>
              <p>{course.duration} hours</p>
              <p>{course.requirements}</p>
              <button onClick={() => handleUpdateCourse(course.ID)}>Update Course</button>
              <button onClick={() => handleDeleteCourse(course.ID)}>Delete Course</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ManageCourses;
