import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createCourse } from '../services/courseService';

const CreateCourse = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [instructor, setInstructor] = useState('');
  const [duration, setDuration] = useState(0);
  const [requirements, setRequirements] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const course = { title, description, instructor, duration, requirements };
    console.log('Creating course:', course); // Log para verificar los datos del curso
    try {
      await createCourse(course, localStorage.getItem('token'));
      navigate('/manage-courses');
    } catch (error) {
      console.error('Error creating course:', error); // Log para errores
    }
  };

  return (
    <div>
      <h2>Create Course</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </div>
        <div>
          <label>Instructor:</label>
          <input type="text" value={instructor} onChange={(e) => setInstructor(e.target.value)} required />
        </div>
        <div>
          <label>Duration (hours):</label>
          <input type="number" value={duration} onChange={(e) => setDuration(Number(e.target.value))} required />
        </div>
        <div>
          <label>Requirements:</label>
          <input type="text" value={requirements} onChange={(e) => setRequirements(e.target.value)} required />
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default CreateCourse;
