import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getCourseById, enrollInCourse } from '../services/courseService';
import './CourseDetail.css';

const CourseDetail = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [enrolled, setEnrolled] = useState(false);
  const [showCongrats, setShowCongrats] = useState(false);
  const [comment, setComment] = useState("");
  const [file, setFile] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const data = await getCourseById(id);
        setCourse(data);
      } catch (error) {
        console.error('Error fetching course:', error);
      }
    };

    fetchCourse();
  }, [id]);

  const handleEnroll = async () => {
    try {
      await enrollInCourse(parseInt(id), token);
      setEnrolled(true);
      setShowCongrats(true);
    } catch (error) {
      console.error('Error enrolling in course:', error);
      alert('You are already enrolled in this course.');
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch(`/courses/${id}/comments`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ comment })
      });
      setComment(""); // Clear the comment field
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleFileSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", file);
    try {
      await fetch(`/courses/${id}/files`, {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${token}`
        },
        body: formData
      });
      setFile(null); // Clear the file field
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div className="course-detail-container">
      <div className="course-detail-card">
        <h1 className="course-title">{course.title}</h1>
        <p className="course-description">{course.description}</p>
        <p className="course-instructor">Instructor: {course.instructor}</p>
        <p className="course-duration">Duration: {course.duration} hours</p>
        <p className="course-requirements">Requirements: {course.requirements}</p>
        {!enrolled && (
          <button className="enroll-button" onClick={handleEnroll}>
            Enroll in this course
          </button>
        )}
        {showCongrats && (
          <div className="congrats-message">
            Congrats, you have been enrolled!
          </div>
        )}

        <form onSubmit={handleCommentSubmit}>
          <div>
            <label>Comment:</label>
            <input
              type="text"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              required
            />
          </div>
          <button type="submit">Add Comment</button>
        </form>

        <form onSubmit={handleFileSubmit}>
          <div>
            <label>Upload File:</label>
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              required
            />
          </div>
          <button type="submit">Upload File</button>
        </form>
      </div>
    </div>
  );
};

export default CourseDetail;
