import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getCourseById, updateCourse } from '../services/courseService';

const EditCourse = () => {
  const { id } = useParams();
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [instructor, setInstructor] = useState('');
  const [duration, setDuration] = useState(0);
  const [requirements, setRequirements] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCourse = async () => {
      const data = await getCourseById(id);
      setTitle(data.title);
      setDescription(data.description);
      setInstructor(data.instructor);
      setDuration(data.duration);
      setRequirements(data.requirements);
    };

    fetchCourse();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const course = { title, description, instructor, duration, requirements };
    await updateCourse(id, course, localStorage.getItem('token'));
    navigate('/manage-courses');
  };

  return (
    <div>
      <h2>Edit Course</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </div>
        <div>
          <label>Instructor:</label>
          <input type="text" value={instructor} onChange={(e) => setInstructor(e.target.value)} required />
        </div>
        <div>
          <label>Duration (hours):</label>
          <input type="number" value={duration} onChange={(e) => setDuration(Number(e.target.value))} required />
        </div>
        <div>
          <label>Requirements:</label>
          <input type="text" value={requirements} onChange={(e) => setRequirements(e.target.value)} required />
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default EditCourse;
