import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL + '/courses';

// Función para obtener todos los cursos
const getCourses = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error('Error fetching courses:', error);
    throw error;
  }
};

// Función para obtener un curso por ID
const getCourseById = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching course:', error);
    throw error;
  }
};

// Función para crear un nuevo curso
const createCourse = async (course, token) => {
  try {
    const response = await axios.post(API_URL, course, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error creating course:', error);
    throw error;
  }
};

// Función para actualizar un curso existente
const updateCourse = async (id, course, token) => {
  try {
    const response = await axios.put(`${API_URL}/${id}`, course, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error updating course:', error);
    throw error;
  }
};

// Función para eliminar un curso
const deleteCourse = async (id, token) => {
  try {
    await axios.delete(`${API_URL}/${id}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
  } catch (error) {
    console.error('Error deleting course:', error);
    throw error;
  }
};

// Función para obtener los cursos de un usuario
const getUserCourses = async (token) => {
  try {
    const response = await axios.get(process.env.REACT_APP_API_URL + '/my-courses', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching user courses:', error);
    throw error;
  }
};

export {
  getCourses,
  getCourseById,
  createCourse,
  updateCourse,
  deleteCourse,
  getUserCourses
};
