import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import CourseDetail from './components/CourseDetail';
import MyCourses from './components/MyCourses';
import ManageCourses from './components/ManageCourses';
import CreateCourse from './components/CreateCourse';
import EditCourse from './components/EditCourse';
import Navigation from './components/Navigation';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [role, setRole] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedRole = localStorage.getItem('role');
    setLoggedIn(!!token);
    setRole(storedRole || ''); // Verificar que el rol se obtenga correctamente
    console.log('Stored role:', storedRole); // Log para verificar el rol
  }, []);

  const handleLogin = () => {
    setLoggedIn(true);
    setRole(localStorage.getItem('role')); // Establecer el rol después del login
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setRole('');
    localStorage.removeItem('token');
    localStorage.removeItem('role');
  };

  return (
    <Router>
      <div>
        <Navigation loggedIn={loggedIn} role={role} onLogout={handleLogout} />
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/home" element={<Home />} />
          <Route path="/courses/:id" element={<CourseDetail />} />
          <Route path="/my-courses" element={role === 'alumno' ? <MyCourses /> : <Navigate to="/login" />} />
          <Route path="/manage-courses" element={role === 'administrador' ? <ManageCourses /> : <Navigate to="/login" />} />
          <Route path="/create-course" element={role === 'administrador' ? <CreateCourse /> : <Navigate to="/login" />} />
          <Route path="/edit-course/:id" element={role === 'administrador' ? <EditCourse /> : <Navigate to="/login" />} />
          <Route path="/" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
