import React from 'react';
import { Link } from 'react-router-dom';

// Componente de navegación
const Navigation = ({ loggedIn, onLogout }) => {
  const role = localStorage.getItem('role'); // Obtener el rol del almacenamiento local
  console.log('User role:', role); // Log para verificar el rol

  return (
    <nav>
      <ul>
        {loggedIn && (
          <>
            {role === 'alumno' && (
              <>
                <li><Link to="/my-courses">My Courses</Link></li>
                <li><Link to="/home">Home</Link></li>
              </>
            )}
            {role === 'administrador' && (
              <>
                <li><Link to="/home">Home</Link></li>
                <li><Link to="/manage-courses">Manage Courses</Link></li>
              </>
            )}
            <li><button onClick={onLogout}>Logout</button></li>
          </>
        )}
        {!loggedIn && (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/register">Register</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Navigation;
