import React, { useState, useEffect } from 'react';
import { getCourses, createCourse, updateCourse, deleteCourse } from '../services/courseService';

const ManageCourses = () => {
  const [courses, setCourses] = useState([]);
  const [editingCourse, setEditingCourse] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [instructor, setInstructor] = useState('');
  const [duration, setDuration] = useState('');
  const [requirements, setRequirements] = useState('');

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    const data = await getCourses();
    setCourses(data);
  };

  const handleSave = async () => {
    const courseData = { title, description, instructor, duration, requirements };
    if (editingCourse) {
      await updateCourse(editingCourse.id, courseData);
    } else {
      await createCourse(courseData);
    }
    setEditingCourse(null);
    fetchCourses();
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setTitle(course.title);
    setDescription(course.description);
    setInstructor(course.instructor);
    setDuration(course.duration);
    setRequirements(course.requirements);
  };

  const handleDelete = async (courseId) => {
    await deleteCourse(courseId);
    fetchCourses();
  };

  return (
    <div>
      <h2>Manage Courses</h2>
      <form onSubmit={(e) => { e.preventDefault(); handleSave(); }}>
        <div>
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </div>
        <div>
          <label>Instructor:</label>
          <input type="text" value={instructor} onChange={(e) => setInstructor(e.target.value)} required />
        </div>
        <div>
          <label>Duration (hours):</label>
          <input type="number" value={duration} onChange={(e) => setDuration(e.target.value)} required />
        </div>
        <div>
          <label>Requirements:</label>
          <textarea value={requirements} onChange={(e) => setRequirements(e.target.value)} required />
        </div>
        <button type="submit">Save</button>
      </form>

      <h3>Course List</h3>
      <ul>
        {courses.map(course => (
          <li key={course.id}>
            <h4>{course.title}</h4>
            <button onClick={() => handleEdit(course)}>Edit</button>
            <button onClick={() => handleDelete(course.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageCourses;
