import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL;

export const getCourses = async () => {
  const response = await axios.get(`${API_URL}/courses`);
  return response.data;
};

export const getCourseById = async (id) => {
  const response = await axios.get(`${API_URL}/courses/${id}`);
  return response.data;
};

export const enrollInCourse = async (courseId, token) => {
  const response = await axios.post(`${API_URL}/enroll`, { course_id: courseId }, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};

export const unenrollFromCourse = async (courseId, token) => {
  const response = await axios.post(`${API_URL}/unenroll`, { course_id: courseId }, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};

export const getUserCourses = async (token) => {
  const response = await axios.get(`${API_URL}/my-courses`, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};

export const createCourse = async (course, token) => {
  const response = await axios.post(API_URL, course, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};

export const updateCourse = async (id, course, token) => {
  const response = await axios.put(`${API_URL}/${id}`, course, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};

export const deleteCourse = async (id, token) => {
  const response = await axios.delete(`${API_URL}/${id}`, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};
