import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getCourseById, enrollInCourse, unenrollFromCourse } from '../services/courseService';

const CourseDetail = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [enrolled, setEnrolled] = useState(false);
  const token = localStorage.getItem('token'); // Obtener el token del almacenamiento local

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const data = await getCourseById(id);
        setCourse(data);
      } catch (error) {
        console.error('Error fetching course:', error);
      }
    };

    fetchCourse();
  }, [id]);

  const handleEnroll = async () => {
    try {
      await enrollInCourse(parseInt(id), token);
      setEnrolled(true); // Actualizar el estado para indicar que el usuario se ha inscrito
    } catch (error) {
      console.error('Error enrolling in course:', error);
      alert('You a');
    }
  };

  const handleUnenroll = async () => {
    try {
      await unenrollFromCourse(parseInt(id), token);
      setEnrolled(false); // Actualizar el estado para indicar que el usuario se ha desinscrito
    } catch (error) {
      console.error('Error unenrolling from course:', error);
      alert('There was an error unenrolling from the course. Please try again.');
    }
  };

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{course.title}</h1>
      <p>{course.description}</p>
      <p>Instructor: {course.instructor}</p>
      <p>Duration: {course.duration} hours</p>
      {!enrolled ? (
        <button onClick={handleEnroll}>Enroll in this course</button>
      ) : (
        <div>
          <p>You are enrolled in this course</p>
          <button onClick={handleUnenroll}>Unenroll from this course</button>
        </div>
      )}
    </div>
  );
};

export default CourseDetail;
