import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL + '/courses';

const enrollInCourse = async (courseId, token) => {
  console.log('Token:', token);  // Log para verificar el token
  try {
    const response = await axios.post(`${process.env.REACT_APP_API_URL}/enroll`, { course_id: courseId }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    console.log('Enroll Response:', response.data); // Añadir log para depurar la respuesta
    return response.data;
  } catch (error) {
    console.error('Error enrolling in course:', error);
    throw error;
  }
};

export { enrollInCourse };
