import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getCourseById, enrollInCourse, getCommentsByCourse, addComment, uploadFile } from '../services/courseService';
import './CourseDetail.css';

const CourseDetail = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [enrolled, setEnrolled] = useState(false);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const data = await getCourseById(id);
        setCourse(data);
      } catch (error) {
        console.error('Error fetching course:', error);
      }
    };

    const fetchComments = async () => {
      try {
        const data = await getCommentsByCourse(id);
        setComments(data);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchCourse();
    fetchComments();
  }, [id]);

  const handleEnroll = async () => {
    try {
      await enrollInCourse(parseInt(id), token);
      setEnrolled(true);
      alert('Congrats, you have been enrolled!');
    } catch (error) {
      console.error('Error enrolling in course:', error);
      alert('You are already enrolled in this course.');
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      const comment = { course_id: parseInt(id), content: newComment };
      await addComment(comment, token);
      setNewComment('');
      const data = await getCommentsByCourse(id);
      setComments(data);
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('course_id', id);
    try {
      await uploadFile(formData, token);
      setSelectedFile(null);
      alert('File uploaded successfully!');
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div className="course-detail-container">
      <div className="course-detail-card">
        <h1 className="course-title">{course.title}</h1>
        <p className="course-description">{course.description}</p>
        <p className="course-instructor">Instructor: {course.instructor}</p>
        <p className="course-duration">Duration: {course.duration} hours</p>
        <p className="course-requirements">Requirements: {course.requirements}</p>
        {!enrolled && (
          <button className="enroll-button" onClick={handleEnroll}>
            Enroll in this course
          </button>
        )}
        <div className="comments-section">
          <h2>Comments</h2>
          <form onSubmit={handleCommentSubmit}>
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add a comment"
              required
            />
            <button type="submit">Submit</button>
          </form>
          <ul>
            {comments.map((comment) => (
              <li key={comment.ID}>
                <p>{comment.content}</p>
                <p>By: {comment.user.name}</p>
              </li>
            ))}
          </ul>
        </div>
        <div className="file-upload-section">
          <h2>Upload Files</h2>
          <form onSubmit={handleFileUpload}>
            <input
              type="file"
              onChange={(e) => setSelectedFile(e.target.files[0])}
              required
            />
            <button type="submit">Upload</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
