import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register'; // Importar el componente Register
import CourseDetail from './components/CourseDetail';
import MyCourses from './components/MyCourses';
import Navigation from './components/Navigation';
import ProtectedRoute from './components/ProtectedRoute'; // Importar el componente ProtectedRoute

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setLoggedIn(!!token);
  }, []);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    localStorage.removeItem('token');
  };

  return (
    <Router>
      <div>
        <Navigation loggedIn={loggedIn} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={loggedIn ? <Navigate to="/home" /> : <Navigate to="/login" />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          <ProtectedRoute path="/home" element={Home} loggedIn={loggedIn} />
          <ProtectedRoute path="/courses/:id" element={CourseDetail} loggedIn={loggedIn} />
          <ProtectedRoute path="/my-courses" element={MyCourses} loggedIn={loggedIn} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
